mapper.select:
SELECT t.*,u.user_name FROM btc_user_income_record t,btc_user_info u WHERE t.user_id=u.id

	AND u.user_name like '%${userName}%'


	AND t.coin_code like '%${coinCode}%'


	AND t.bind_no =#{bindNo}


	AND t.user_id =#{userId}


	AND date(t.gmt_create) >=#{beginDate}


	AND date(t.gmt_create) <=#{endDate}


	ORDER BY t.${orderBy} ${order}

SELECT count(1) FROM btc_user_income_record t,btc_user_info u WHERE t.user_id=u.id

	AND u.user_name like '%${userName}%'


	AND t.coin_code like '%${coinCode}%'


	AND t.bind_no =#{bindNo}


	AND t.user_id =#{userId}


	AND date(t.gmt_create) >=#{beginDate}


	AND date(t.gmt_create) <=#{endDate}

mapper.insert:

insert into btc_user_income_record (id, user_id, account_id, 
  coins, coin_income, coin_day_rate, 
  coin_year_rate, coin_code, usdt_unit, 
  usdt_income, bind_no, gmt_create
  )
values (#{id,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}, #{accountId,jdbcType=BIGINT}, 
  #{coins,jdbcType=DECIMAL}, #{coinIncome,jdbcType=DECIMAL}, #{coinDayRate,jdbcType=DECIMAL}, 
  #{coinYearRate,jdbcType=DECIMAL}, #{coinCode,jdbcType=VARCHAR}, #{usdtUnit,jdbcType=DECIMAL}, 
  #{usdtIncome,jdbcType=DECIMAL}, #{bindNo,jdbcType=VARCHAR}, #{gmtCreate,jdbcType=TIMESTAMP}
  )
