mapper.select:
SELECT t.*,u.user_name FROM btc_user_income_verify_temp t,btc_user_info u WHERE t.user_id=u.id

	AND u.user_name like '%${userName}%'


	AND t.coin_code like '%${coinCode}%'


	AND date(t.count_date) >=#{beginDate}


	AND date(t.count_date) <=#{endDate}


	ORDER BY t.${orderBy} ${order}

SELECT count(1) FROM btc_user_income_verify_temp t,btc_user_info u WHERE t.user_id=u.id

	AND u.user_name like '%${userName}%'


	AND t.coin_code like '%${coinCode}%'


	AND date(t.count_date) >=#{beginDate}


	AND date(t.count_date) <=#{endDate}


	ORDER BY t.${orderBy} ${order}
